def swaps(arrs)
	result = []
	arrs.each do |arr|
		n = arr.length - 1
		swap_count = 0
		chaotic = false
		while n > 0 
			if arr[n] != n + 1
				if arr[n-1] == n + 1
					swap_count += 1
					arr[n-1], arr[n] = arr[n], arr[n-1]
				elsif arr[n-2] == n + 1
					swap_count += 2
					arr[n-2], arr[n-1], arr[n] = arr[n-1], arr[n], arr[n-2]
				else
					result << "Too chaotic"
					chaotic = true
					break
				end
			end

			n -= 1
		end

		result << swap_count unless chaotic	
	end

	result
end
